  const fetchBlogPosts = async () => {
    setLoadingBlogs(true);
    setBlogError(null);
    try {
      const params = new URLSearchParams({
        page: blogFilters.page.toString(),
        limit: blogFilters.limit.toString(),
        ...(blogFilters.status !== 'all' && { status: blogFilters.status }),
        ...(blogFilters.category !== 'all' && { category: blogFilters.category })
      });

      const response = await fetch(`/api/blog?${params}`);
      const data = await response.json();

      if (data.success) {
        setBlogPosts(data.data.posts);
        setBlogPagination(data.data.pagination);
      } else {
        setBlogError('Failed to load blog posts');
      }
    } catch (error) {
      setBlogError('Failed to load blog posts');
    } finally {
      setLoadingBlogs(false);
    }
  };

  const createBlogPost = async (postData: any) => {
    setSavingBlog(true);
    try {
      const response = await fetch('/api/blog', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(postData),
      });

      const data = await response.json();

      if (data.success) {
        setShowBlogModal(false);
        setBlogForm({
          title: '',
          slug: '',
          excerpt: '',
          content: '',
          featuredImage: '',
          tags: [],
          category: 'General',
          authorName: 'Saransh Nimje',
          authorAvatar: '',
          readTime: 5,
          status: 'draft'
        });
        fetchBlogPosts();
        return { success: true };
      } else {
        return { success: false, message: data.message };
      }
    } catch (error) {
      return { success: false, message: 'Failed to create blog post' };
    } finally {
      setSavingBlog(false);
    }
  };

  const updateBlogPost = async (id: string, postData: any) => {
    setSavingBlog(true);
    try {
      const response = await fetch(`/api/blog/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(postData),
      });

      const data = await response.json();

      if (data.success) {
        setShowBlogModal(false);
        setSelectedBlog(null);
        setBlogForm({
          title: '',
          slug: '',
          excerpt: '',
          content: '',
          featuredImage: '',
          tags: [],
          category: 'General',
          authorName: 'Saransh Nimje',
          authorAvatar: '',
          readTime: 5,
          status: 'draft'
        });
        fetchBlogPosts();
        return { success: true };
      } else {
        return { success: false, message: data.message };
      }
    } catch (error) {
      return { success: false, message: 'Failed to update blog post' };
    } finally {
      setSavingBlog(false);
    }
  };

  const deleteBlogPost = async (id: string) => {
